<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(		'name'		=> str_replace("'", "\'", trim($_POST['name'])),		'de_name'	=> str_replace("'", "\'", trim($_POST['name'])),		'ru_name'	=> str_replace("'", "\'", trim($_POST['name'])),		'cz_name'	=> str_replace("'", "\'", trim($_POST['name'])),		'sk_name'	=> str_replace("'", "\'", trim($_POST['name'])),		'tr_name'	=> str_replace("'", "\'", trim($_POST['name'])),		'es_name'	=> str_replace("'", "\'", trim($_POST['name'])),		'ar_name'	=> str_replace("'", "\'", trim($_POST['name'])),		'alias'				=> $_POST['alias'],		'parent_id'			=> (int)$_POST['parent_id'],		'order_id'			=> (int)$_POST['order_id'],		'block'				=> $_POST['block'][0],		'show_header'		=> $_POST['show_header'][0],		'show_footer'		=> $_POST['show_footer'][0],		'show_footer_2'		=> $_POST['show_footer_2'][0],		'signedin_only'		=> $_POST['signedin_only'][0],		'created'			=> date("Y-m-d H:i:s", time()),		'modified'			=> date("Y-m-d H:i:s", time()),	);					if(strlen($cardUpd['name'])>1) {			$query = "INSERT INTO [pre]$appTable ";		$fieldsStr = " ( ";		$valuesStr = " ( ";		$cntUpd = 0;		foreach($cardUpd as $field => $itemUpd) {			$cntUpd++;			$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");			$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");		}				$fieldsStr .= " ) ";		$valuesStr .= " ) ";		$query .= $fieldsStr." VALUES ".$valuesStr;					$item_id = $ah->dbh->q($query,0,1,1);				if($item_id) {			$data['item_id'] = $item_id;			$data['status'] = "success";			$data['message'] = "Done";		} else {			$data['item_id'] = 0;		}	}else{		$data['status'] = "failed";		$data['message'] = "Specify display name";	}	