<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(			'name'		=> strip_tags(trim($_POST['name'])),			'desc'		=> trim($_POST['desc']),			'alias'					=> $_POST['alias'],			'show_on_about'			=> $_POST['show_on_about'][0],	);				$query = "SELECT id FROM [pre]$appTable WHERE `alias`='".$cardUpd['alias']."' AND `id`!=$item_id LIMIT 1";	$test_alias = $ah->rs($query);	if(strlen($cardUpd['name'])>1)	{		if(!$test_alias)		{				$filename = "cover";					if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)				{					$file_path = "../../../../public/img/content/";										$file_update = $ah->mtvc_add_files_file(array(							'path'			=>$file_path,							'name'			=>5,							'pre'			=>"page_",							'size'			=>10,							'rule'			=>0,							'max_w'			=>2500,							'max_h'			=>2500,							'files'			=>$filename						  ));					if($file_update)					{						$cardUpd[$filename] = $file_update;												$curr_filename = $_POST['curr_filename'];												unlink($file_path.$curr_filename);					}				}						// Create main table item								$query = "INSERT INTO [pre]$appTable ";								$fieldsStr = " ( ";								$valuesStr = " ( ";								$cntUpd = 0;				foreach($cardUpd as $field => $itemUpd)				{					$cntUpd++;										$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");										$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");				}								$fieldsStr .= " ) ";								$valuesStr .= " ) ";								$query .= $fieldsStr." VALUES ".$valuesStr;									$item_id = $ah->dbh->q($query,0,1,1);								if($item_id)				{					$data['item_id'] = $item_id;					$data['status'] = "success";					$data['message'] = "Раздел создан.";									}else				{					$data['item_id'] = 0;				}						}else{				$data['status'] = "failed";				$data['message'] = "Раздел с таким алиасом уже существует";			}	}else{		$data['status'] = "failed";		$data['message'] = "Укажите название проекта";	}	