<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];	$item_id = $_POST['item_id'];	$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // empty = iw	$query = "SELECT `alias` FROM [pre]menu WHERE `id`='$item_id' LIMIT 1";	$menu = $ah->dbh->q($query,1);	$cardUpd = array(		$lang_prefix.'name'	=> str_replace("'", "\'", trim($_POST['name'])),		'parent_id'			=> (int)$_POST['parent_id'],		'order_id'			=> (int)$_POST['order_id'],		'block'				=> $_POST['block'][0],		'show_header'		=> $_POST['show_header'][0],		'show_footer'		=> $_POST['show_footer'][0],		'show_footer_2'		=> $_POST['show_footer_2'][0],		'signedin_only'		=> $_POST['signedin_only'][0],		'modified'			=> date("Y-m-d H:i:s", time()),	);	if(isset($_POST['alias'])) {		$cardUpd['alias'] = $_POST['alias'];	}						if( strlen($cardUpd[$lang_prefix.'name']) > 1 ) {		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;		foreach($cardUpd as $field => $itemUpd) {			$cntUpd++;			$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");		}		$query .= " WHERE `id`=$item_id LIMIT 1";		$ah->rs($query);		$data['status'] = "success";		$data['query'] = $query;		$data['message'] = "Saved.";	}else{		$data['status'] = "failed";		$data['message'] = "Specify display name.";	}		