<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable 	= $_POST['appTable'];		$name		= $_POST['name'];	$ru_name	= $_POST['ru_name'];	// Update main table	$ah->rs("TRUNCATE TABLE [pre]$appTable");	foreach($name as $idx => $value)	{		$ua_n = $value;		$ru_n = $ru_name[$idx];		$query = "INSERT INTO [pre]$appTable (`name`, `ru_name`) VALUES ('$ua_n', '$ru_n')";		$ah->rs($query);	}		$data['message'] = "Специализации сохранены";	$data['status'] = "success";	