<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);	$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // (Israel - empty, ru, en, fr)		$cardUpd = array(					'org_name' => $_POST['org_name'],					'contact_name' => $_POST['contact_name'],					'city' => $_POST['city'],					'address' => $_POST['address'],					'zip' => $_POST['zip'],					'phone' => $_POST['phone'],					'site' => $_POST['site'],					'login' => $_POST['login'],					'socials_fb' => $_POST['socials_fb'],					'socials_ln' => $_POST['socials_ln'],					'socials_vk' => $_POST['socials_vk'],					'description' => $_POST['description'],					'spec' => (int)$_POST['spec'],					'block' => $_POST['block'][0],					'confirmed' => $_POST['confirmed'][0],					'agreed' => $_POST['agreed'][0],					'modified'			=> date("Y-m-d H:i:s", time())					);						// File upload 		$filename = "image";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{		$file_path = "../../../../webroot/img/avatars/";		$files_upload = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"upd_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>600,				'resize_h'		=>600,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload) {			$cardUpd[$filename] = $files_upload;		}	}	// Update main table		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";	$data['qq'] = $query;				$ah->rs($query,0,1);		$data['message'] = "Организация успешно сохранена.";	