<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 1;	$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // (Israel - empty, ru, en, fr)		$cardUpd = array(		$lang_prefix.'text_1' => $_POST['text_1'],		$lang_prefix.'text_2' => $_POST['text_2'],		$lang_prefix.'text_3' => $_POST['text_3'],		$lang_prefix.'text_4' => $_POST['text_4'],		$lang_prefix.'text_5' => $_POST['text_5'],		$lang_prefix.'text_6' => $_POST['text_6'],		$lang_prefix.'text_7' => $_POST['text_7'],		$lang_prefix.'text_8' => $_POST['text_8'],		$lang_prefix.'text_9' => $_POST['text_9'],		$lang_prefix.'text_10' => $_POST['text_10'],		$lang_prefix.'text_11' => $_POST['text_11'],		$lang_prefix.'text_12' => $_POST['text_12'],		$lang_prefix.'text_13' => $_POST['text_13'],		$lang_prefix.'text_14' => $_POST['text_14'],		$lang_prefix.'text_15' => $_POST['text_15'],		$lang_prefix.'text_16' => $_POST['text_16'],		$lang_prefix.'text_17' => $_POST['text_17'],		$lang_prefix.'text_18' => $_POST['text_18'],		$lang_prefix.'text_19' => $_POST['text_19'],		$lang_prefix.'text_20' => $_POST['text_20'],		$lang_prefix.'text_21' => $_POST['text_21'],		$lang_prefix.'text_22' => $_POST['text_22'],		$lang_prefix.'text_23' => $_POST['text_23'],		$lang_prefix.'text_24' => $_POST['text_24'],		$lang_prefix.'text_25' => $_POST['text_25'],		$lang_prefix.'text_26' => $_POST['text_26'],		$lang_prefix.'text_27' => $_POST['text_27'],		$lang_prefix.'text_28' => $_POST['text_28'],		$lang_prefix.'text_29' => $_POST['text_29'],		$lang_prefix.'text_30' => $_POST['text_30'],		$lang_prefix.'text_31' => $_POST['text_31'],        $lang_prefix.'meta_title' => $_POST['meta_title'],		$lang_prefix.'meta_keys' => $_POST['meta_keys'],		$lang_prefix.'meta_desc' => $_POST['meta_desc'],		'indexing' => $_POST['indexing'][0],	);						// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	fore