<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 1;	$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // (Israel - empty, ru, en, fr)		$cardUpd = array(					$lang_prefix.'caption' => $_POST['caption'],					$lang_prefix.'btn_text' => $_POST['btn_text'],					$lang_prefix.'input_text' => $_POST['input_text'],					$lang_prefix.'select1_text' => $_POST['select1_text'],					$lang_prefix.'select2_text' => $_POST['select2_text'],					$lang_prefix.'select3_text' => $_POST['select3_text'],					$lang_prefix.'radio_text' => $_POST['radio_text'],					$lang_prefix.'radio2_text' => $_POST['radio2_text'],					$lang_prefix.'radio_title' => $_POST['radio_title'],					$lang_prefix.'details' => $_POST['details'],					$lang_prefix.'details2' => $_POST['details2'],					$lang_prefix.'item_text1' => $_POST['item_text1'],					$lang_prefix.'item_text2' => $_POST['item_text2'],					$lang_prefix.'item_text3' => $_POST['item_text3'],					$lang_prefix.'item_text4' => $_POST['item_text4'],					$lang_prefix.'item_text5' => $_POST['item_text5'],					$lang_prefix.'card_text1' => $_POST['card_text1'],					$lang_prefix.'card_text2' => $_POST['card_text2'],					$lang_prefix.'card_text3' => $_POST['card_text3'],					$lang_prefix.'card_text4' => $_POST['card_text4'],					$lang_prefix.'card_text5' => $_POST['card_text5'],					$lang_prefix.'card_text6' => $_POST['card_text6'],					$lang_prefix.'card_text7' => $_POST['card_text7'],					$lang_prefix.'card_text8' => $_POST['card_text8'],					$lang_prefix.'card_text9' => $_POST['card_text9'],					$lang_prefix.'card_text10' => $_POST['card_text10'],					$lang_prefix.'card_text11' => $_POST['card_text11'],					$lang_prefix.'card_text12' => $_POST['card_text12'],					$lang_prefix.'card_text13' => $_POST['card_text13'],					$lang_prefix.'card_text14' => $_POST['card_text14'],					$lang_prefix.'card_text15' => $_POST['card_text15'],					$lang_prefix.'card_text16' => $_POST['card_text16'],					$lang_prefix.'card_text17' => $_POST['card_text17'],					$lang_prefix.'modal_text1' => $_POST['modal_text1'],					$lang_prefix.'modal_text2' => $_POST['modal_text2'],					$lang_prefix.'modal_text3' => $_POST['modal_text3'],					$lang_prefix.'modal_text4' => $_POST['modal_text4'],					$lang_prefix.'modal_text5' => $_POST['modal_text5'],										'index' 	=> (int)$_POST['index'][0],										$lang_prefix.'meta_title'	=> $_POST['meta_title'],					$lang_prefix.'meta_keys' 	=> $_POST['meta_keys'],					$lang_prefix.'meta_desc' 	=> $_POST['meta_desc'],										);						// File upload 		$filename = "filename";		if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{		$file_path = "../../../../webroot/files/content/";				$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"page_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;						$curr_filename = $_POST['curr_filename'];						unlink($file_path.$curr_filename);		}	}		// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);		$data['q'] = $query;	$data['message'] = "Page Contacts saved successfully.";	