<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 1;	$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // (Israel - empty, ru, en, fr)		$cardUpd = array(					$lang_prefix.'profile_text1' => $_POST['profile_text1'],					$lang_prefix.'profile_text2' => $_POST['profile_text2'],					$lang_prefix.'profile_text3' => $_POST['profile_text3'],					$lang_prefix.'profile_text4' => $_POST['profile_text4'],					$lang_prefix.'profile_text5' => $_POST['profile_text5'],					$lang_prefix.'profile_text6' => $_POST['profile_text6'],					$lang_prefix.'profile_text7' => $_POST['profile_text7'],					$lang_prefix.'profile_text8' => $_POST['profile_text8'],					$lang_prefix.'profile_text9' => $_POST['profile_text9'],					$lang_prefix.'profile_text10' => $_POST['profile_text10'],					$lang_prefix.'profile_text11' => $_POST['profile_text11'],					$lang_prefix.'profile_text12' => $_POST['profile_text12'],					$lang_prefix.'profile_text13' => $_POST['profile_text13'],										'index' 	=> (int)$_POST['index'][0],										$lang_prefix.'meta_title'	=> $_POST['meta_title'],					$lang_prefix.'meta_keys' 	=> $_POST['meta_keys'],					$lang_prefix.'meta_desc' 	=> $_POST['meta_desc'],										);						// File upload 		$filename = "filename";		if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{		$file_path = "../../../../webroot/files/content/";				$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"page_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;						$curr_filename = $_POST['curr_filename'];						unlink($file_path.$curr_filename);		}	}		// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);		$data['q'] = $query;	$data['message'] = "Page saved successfully.";	