<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);	$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // (Israel - empty, ru, en, fr)		$cardUpd = array(					'user_id'			=>(int) $_POST['user_id'],					'project_id'		=> $_POST['project_id'],					'text'				=> $_POST['text'],					'moderation'		=> $_POST['moderation'][0],					'modified'			=> date("Y-m-d H:i:s", time())					);						// File upload 		$filename = "files";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{		$file_path = "../../../../webroot/uploads/";		$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"upd_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>600,				'resize_h'		=>600,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload)		{			$now = date("Y-m-d H:i:s");			foreach($files_upload as $file_upload)			{				$extP = explode('.', $file_upload);				$ext = end($extP);				$query = "INSERT INTO [pre]projects_responses_files (`response_id`, `filename`, `ext`, `created`) VALUES ('$item_id', '$file_upload', '$ext', '$now')";				$ah->rs($query);			}		}	}	// Update main table		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);		$data['message'] = "Отклик успешно сохранен.";	