<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];	$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // empty = iw	$query = "SELECT `alias` FROM [pre]menu WHERE `id`='$item_id' LIMIT 1";	$menu = $ah->dbh->q($query,1);	$cardUpd = array(		'company_name'				=> strip_tags(trim($_POST['company_name'])),		'text'				=> strip_tags(trim($_POST['text'])),		'block'				=> $_POST['block'][0],		'modified'			=> date("Y-m-d H:i:s", time()),	);		// File upload 		$filename = "company_logo";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{		$file_path = "../../../../webroot/img/content/";		$files_upload = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"upd_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>600,				'resize_h'		=>600,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload) {			$cardUpd[$filename] = $files_upload;		}	}	$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";	$data['query'] = $query;	$ah->rs($query);		$data['message'] = "Партнер успешно сохранен.";