<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];		$rows = $_POST['table'];	$cardUpd = array(					'name'			=> $_POST['name'],					'columns'		=> (int)$_POST['columns'],					'block'			=> (int)$_POST['block'][0],										'col1'			=> (int)$_POST['col1'][0],					'col2'			=> (int)$_POST['col2'][0],					'col3'			=> (int)$_POST['col3'][0],					'col4'			=> (int)$_POST['col4'][0],					'col5'			=> (int)$_POST['col5'][0],					'col6'			=> (int)$_POST['col6'][0],					'col7'			=> (int)$_POST['col7'][0],					'col8'			=> (int)$_POST['col8'][0],					'col9'			=> (int)$_POST['col9'][0],					'col10'			=> (int)$_POST['col10'][0]					);							// Update main table		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";	$ah->rs($query);				// Update table rows		if($item_id)	{		$q = "DELETE FROM [pre]service_tables_rows WHERE `table_id`='$item_id'";		$ah->rs($q,0,1);	}		foreach($rows as $row_id => $columns)	{		$_fields = "(`table_id` ";		$_values = "('$item_id' ";				foreach($columns as $j => $col)		{			$_index = $j+1;			$_fields .= ", `col".$_index."`";			$_values .= ", '".$col."'";		}				$q = "INSERT INTO [pre]service_tables_rows $_fields ) VALUES $_values)";				$ah->rs($q,0,1);				// echo "<pre>"; print_r($q); echo "</pre>";	}	$data['status'] = "failed";	$data['message'] = "Table saved success.";					