<?php 	// Start header content	$headParams = array( 'parent'=>$parent, 'alias'=>$alias, 'id'=>$id, 'item_id'=>$item_id, 'appTable'=>$appTable );		$data['headContent'] = $zh->getCardViewHeader($headParams, $lpx);	$pref = ($lpx ? $lpx.'_' : '');		// Start body content		$cardItem = $zh->getMenuItem($item_id, $lpx);	$langs = $zh->getAvailableLangs();	$rootPath = ROOT_PATH;		$cardTmp = array(		 'ID'								=>	array( 'type'=>'text', 	'field'=>'id', 				'params'=>array() ),		 'Menu name'						=>	array( 'type'=>'text', 	'field'=>$pref.'name', 		'params'=>array() ),		 'Alias'							=>	array( 'type'=>'text', 	'field'=>'alias', 			'params'=>array() ),		 'Display number'					=>	array( 'type'=>'text', 	'field'=>'order_id', 		'params'=>array() ),		 'Publication'						=>	array( 'type'=>'text', 	'field'=>'block', 			'params'=>array( 'replace'=>array('0'=>'Yes', '1'=>'No') ) ),		 'Display in header'				=>	array( 'type'=>'text', 	'field'=>'show_header', 	'params'=>array( 'replace'=>array('1'=>'Yes', '0'=>'No') ) ),		 'Display in footer'				=>	array( 'type'=>'text', 	'field'=>'show_footer', 	'params'=>array( 'replace'=>array('1'=>'Yes', '0'=>'No') ) ),		 'Display in footer submenu'		=>	array( 'type'=>'text', 	'field'=>'show_footer_2', 	'params'=>array( 'replace'=>array('1'=>'Yes', '0'=>'No') ) ),		 'Display only for signed in users'	=>	array( 'type'=>'text', 	'field'=>'signedin_only', 	'params'=>array( 'replace'=>array('1'=>'Yes', '0'=>'No') ) ),		 'Created at'						=>	array( 'type'=>'date', 	'field'=>'created', 		'params'=>array() ),		 'Edited at'						=>	array( 'type'=>'date', 	'field'=>'modified', 		'params'=>array() )	 );	$cardViewTableParams = array( 'cardItem'=>$cardItem, 'cardTmp'=>$cardTmp, 'rootPath'=>$rootPath, 'lpx'=>$lpx, 'headParams'=>$headParams, 'langs'=>$langs );		$cardViewTableStr = $zh->getCardViewTable($cardViewTableParams);		// Join content		$data['bodyContent'] .= "		<div class='ipad-20' id='order_conteinter'>			<h3>MENU #$item_id