->getCardEditForm($cardEditFormParams);	// Feedbacks prepare start	$itemsList = $zh->getResponseFeedbacks($params,$item_id);	$totalItems = $zh->getResponseFeedbacks($params,$item_id,true);		$on_page = (isset($_COOKIE['global_on_page']) ? $_COOKIE['global_on_page'] : GLOBAL_ON_PAGE);	$pages = ceil($totalItems/$on_page);	$start_page = (isset($params['start']) ? $params['start'] : 1);		$frst_page = 1;	$prev_page = 1;	$next_page = $pages;	$last_page = $pages;					if($start_page < $pages) $next_page = $start_page+1;	if($start_page > 1) $prev_page = $start_page-1;		$tableColumns = [		' '					=>	['type'=>'text',		'field'=>'&nbsp'],		'Организация'		=>	['type'=>'text',		'field'=>'organization'],		'Ответ'				=>	['type'=>'text',		'field'=>'text'],	    'Модерация'			=>	['type'=>'text',		'field'=>'moderation',                           																			'params'=>[												'type'=>'ajaxColumn',												'alias'=>'Ostatus',												'form'=>'trigger',												'data'=>['1'=>'Да','0'=>'Нет'],												'active'=>'moderation',												'clickFunction'=>'ResponsesFeedbacksBlockUpdate'											]],		];		$headParams = [		'parent'	=>	$parent, 		'alias'		=>	'responses-list',		'id'		=>	71, 		'item_id'	=>	1, 		'appTable'	=>	'osc_projects_responses'	];	$tableParams = [		'itemsList'		=>	$itemsList, 		'tableColumns'	=>	$tableColumns, 		'headParams'	=>	$headParams	];		$tableStr = $zh->getItemsTable($tableParams);		$pagiParams = [		'headParams'=>	$headParams, 		'start_page'=>	$start_page, 		'pages'		=>	$pages, 		'on_page'	=>	$on_page	];		$pagiStr = $zh->getLandingPagination($pagiParams);	// Feedbacks prepare end	$data['bodyContent'] .= "<div class='ipad-20' id='order_conteinter'><h3 class='new-line'>Редактирование отклика #$item_id</h3>";	$data['bodyContent'] .= $cardEditFormStr;	$data['bodyContent'] .=	"</div>";	$data['bodyContent'] .= "</div><!-- landCluster --><div class='divider'></div>";	$data['bodyContent'] .= "<h2