<?php 	// Start header content	$headParams = array( 'parent'=>$parent, 'alias'=>$alias, 'id'=>$id, 'item_id'=>1, 'appTable'=>$appTable );		$data['headContent'] = $zh->getLandingHeader($headParams);		// Set lultilanguage FIELDS (Israel - empty, ru, en, fr)		$lang_fields = array(        'text_1',        'text_2',	);		ob_start();	$cardData = $zh->getPageItem($pageTable, $lpx, $lang_fields);	$sqlLog = ob_get_contents();	ob_get_clean();		$langs = $zh->getAvailableLangs();	$lpx_name = strtoupper($lpx ? $lpx : DEF_LANG);		$cardItem = $cardData['cardItem'];	$menuInfo = $cardData['menuInfo'];		// Join Content		$data['bodyContent'] = ""; // $filterFormStr;		// new block		$data['bodyContent'] .= "		<div id='landCluster' class='animatedX'>			<h3 class='new-line'>Header settings</b></h3>	";		$rootPath = ROOT_PATH;		$cardTmp = array(		 'LPX'		=>	array( 'type'=>'hidden',	'field'=>'lpx', 'value'=>$lpx ), // multilanguage important field	     '['.$lpx_name.'] '.'Text 1'	=>	array( 'type'=>'input', 'field'=>'text_1', 'params'=>array('size'=>100, 'hold'=>'Header text' ) ),	     '['.$lpx_name.'] '.'Text 2'	=>	array( 'type'=>'input', 'field'=>'text_2', 'params'=>array('size'=>100, 'hold'=>'Header text' ) ),	);	$cardEditFormParams = array( 'cardItem'=>$cardItem, 'cardTmp'=>$cardTmp, 'rootPath'=>$rootPath, 'actionName'=>"editPageHeader", 'ajaxFolder'=>'edit', 'appTable'=>$pageTable, 'lpx'=>$lpx, 'headParams'=>$headParams, 'langs'=>$langs, 'clickUpdate'=>'landingPage' );		$cardEditFormStr = $zh->getCardEditForm($cardEditFormParams, true);		$data['bodyContent'] .= $cardEditFormStr;		ob_start();		if($sqlLog) echo "<pre>"; print_r($sqlLog); echo "</pre>";		$data['bodyContent'] .= ob_get_contents();	ob_get_clean();		$data['bodyContent'] .= "</div><!-- landCluster --><div class='divider'></div>";		// end new block	?>