<?php	/*	KLYCHA WEB TECHNOLOGIES	*/	/*	***************************	*/	/*	Author: Sivkovich Maxim		*/	/*	***************************	*/	/*	Developed: from 2013		*/	/*	***************************	*/		// Settings class	require("BasicHelp.php");class homesectionsHelp extends BasicHelp{   		public $dbh;				public $table;		public $id;		public $item;				public function __construct($dbh)		{			parent::__construct($dbh);			$this->dbh = $dbh;		} 				// Get All Langs		public function getAllLangs(){			$q = "				SELECT * FROM [pre]languages WHERE `used` = 0 LIMIT 1000 			";			return $this->rs($q);		}				// Get Available Langs		public function getAvailableLangs(){				$query = "					SELECT L.name, L.alias, M.block, M.id 					FROM [pre]site_languages AS M 					LEFT JOIN [pre]languages AS L ON L.id = M.lang_id 					WHERE 1 $filter_and AND L.alias != '".DEF_LANG."'					ORDER BY M.id					LIMIT 1000				";										return $this->rs($query);		}				// Get Section item		public function getHomeSectionItem($tablename, $lpx="", $lang_fields=array())		{			$result = ['cardItem'=>[], 'menuInfo'=>[]];						$query = "				SELECT M.*				FROM [pre]home_sections_ref as R 				LEFT JOIN [pre]menu_home AS M ON M.id = R.section_id 				WHERE 					R.table_name='$tablename' 				LIMIT 1			";			$result['menuInfo'] = $this->rs($query,1);									$langFields = "";			$lang_prefix = ($lpx ? $lpx."_" : "");						foreach($lang_fields as $field)			{				$langFields .= ", M.".$lang_prefix.$field." as ".$field." ";			}						$query = "				SELECT M.* $langFields  				FROM `$tablename` as M 				WHERE 					`id`='1' 				LIMIT 1			";			$result['cardItem'] = $this->rs($query,1);						// echo "<pre>"; print_r($query); echo "</pre>";						return $result;		}										// Get Home Why Us item		public function getHomeWhyUsItem($id)		{			$query = "SELECT M.* FROM [pre]home_why_us_items as M WHERE `id`='$id' LIMIT 1";			$resultMassive = $this->rs($query);						$result = ($resultMassive ? $resultMassive[0] : array());						return $result;		}			// Get all Home Why Us Items		public function getHomeWhyUs($params=array(),$typeCount=false)		{			// Filter params						$filter_and = "";						if(isset($params['filtr']['massive']))			{				foreach($params['filtr']['massive'] as $f_name => $f_value)				{					if($f_value < 0) continue;					$filter_and .= " AND ($f_name='$f_value') ";				}			}						// Filter like						if(isset($params['filtr']['filtr_search_key']) && isset($params['filtr']['filtr_search_field']) && trim($params['filtr']['filtr_search_key']) != "")			{				$search_field = $params['filtr']['filtr_search_field'];				$search_key = $params['filtr']['filtr_search_key'];								$filter_and .= " AND ($search_field LIKE '%$search_key%') ";			}						// Filter sort						$sort_field		= (isset($params['filtr']['sort_filtr']) ? $params['filtr']['sort_filtr'] : "M.id");						$sort_vector	= (isset($params['filtr']['order_filtr']) ? $params['filtr']['order_filtr'] : "");						// Order limits						$limit = (isset($_COOKIE['global_on_page']) ? (int)$_COOKIE['global_on_page'] : GLOBAL_ON_PAGE);						if($limit <= 0) $limit = GLOBAL_ON_PAGE;						$start = (isset($params['start']) ? ($params['start']-1)*$limit : 0);						if(!$typeCount)			{							$query = "SELECT M.*									FROM [pre]home_why_us_items as M  												WHERE 1 $filter_and 						ORDER BY $sort_field $sort_vector 						LIMIT $start,$limit";										return $this->rs($query);							}else			{				$query = "SELECT COUNT(*)  									FROM [pre]home_why_us_items as M  												WHERE 1 $filter_and 												LIMIT 100000";										$result = $this->rs($query);				return $result[0]['COUNT(*)'];			}		}								    	public function __destruct(){}}